import './App.css';

function App() {
  return (
<>
<div class="container">
  <h1>Increament/Decreament counter</h1>
  <h4>using React and Redux</h4>
  <div class="quantity">
    <a class="quantity__minus" title='Decreament'><span>-</span></a>
    <input name="quantity" type="text" class="quantity__input" value="0" />
