  const myState = useSelector((state)=> state.changeTheNumber)

  return (
<>
<div className="container">
  <h1>Increament/Decreament counter</h1>
  <h4>using React and Redux</h4>
  <div className="quantity">
    <a className ="quantity__minus" title='Decreament'><span>-</span></a>
    <input name="quantity" type="text" className="quantity__input" value="0"/>
    <a className="quantity__plus" title='Increament'><span>+</span></a>
  </div>
</div>
</>

  );
}

